import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import Header from './Header';
import './LandingPage.css';

const LandingPage = ({ userName }) => {
    const [file, setFile] = useState(null);
    const [summary, setSummary] = useState('');
    const [notes, setNotes] = useState('');
    const [pptContent, setPptContent] = useState('');
    const [loading, setLoading] = useState(false);
    const [submitStatus, setSubmitStatus] = useState('');

    const onDrop = (acceptedFiles) => {
        if (acceptedFiles.length > 0 && (acceptedFiles[0].type === 'text/plain' || acceptedFiles[0].type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')) {
            setFile(acceptedFiles[0]);
            setSubmitStatus('');
        } else {
            alert('Please upload a .txt or .docx file.');
        }
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop, accept: { 'text/plain': ['.txt'], 'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'] } });

    const handleSubmit = async () => {
        if (!file) return alert('Please upload a file first.');
        setLoading(true);
        setSubmitStatus('Submitting...');
        const formData = new FormData();
        formData.append('file', file);
        try {
            const response = await axios.post('/api/submit', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            setSubmitStatus('File submitted successfully!');
            console.log('File submitted:', response.data);
        } catch (error) {
            setSubmitStatus('Failed to submit file. Please try again.');
            console.error('Submit error:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchContent = async (endpoint) => {
        if (!file) return alert('Please upload a file first.');
        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);
        try {
            const response = await axios.post(`/api/${endpoint}`, formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            if (endpoint === 'summary') setSummary(response.data);
            else if (endpoint === 'notes') setNotes(response.data);
            else if (endpoint === 'ppt') setPptContent(response.data);
        } catch (error) {
            console.error('Error fetching content:', error);
        } finally {
            setLoading(false);
        }
    };

    const downloadPPT = () => {
        if (pptContent) {
            const blob = new Blob([pptContent], { type: 'application/vnd.openxmlformats-officedocument.presentationml.presentation' });
            const url = window.URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = 'presentation.pptx';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            window.URL.revokeObjectURL(url);
        }
    };

    return (
        <div className="landing-page">
            <Header userName={userName} />
            <h2>AutoReQ</h2>
            <div className="wrapper-for-drop">
                <div {...getRootProps()} className="dropzone">
                    <input {...getInputProps()} />
                    <p>Drag and drop a .txt or .docx file here</p>
                    {file && <p>{file.name}</p>}
                </div>
            </div>
            <button onClick={handleSubmit} className="submit-button">
                Submit
            </button>
            {submitStatus && <p className="submit-status">{submitStatus}</p>}
            <div className="button-group">
                <button onClick={() => fetchContent('summary')} disabled={loading}>Generate Summary</button>
                <button onClick={() => fetchContent('notes')} disabled={loading}>Generate Notes</button>
                <button onClick={() => fetchContent('ppt')} disabled={loading}>Generate PPT</button>
            </div>
            {(summary || notes || pptContent) && (
                <div className="content-box">
                    {summary && <p>{summary}</p>}
                    {notes && <p>{notes}</p>}
                    {pptContent && (
                        <>
                            <p>Preview: {pptContent}</p>
                            <button onClick={downloadPPT}>Download PPT</button>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default LandingPage;
